%% mdlReadMidi%  Conversion of MIDI file to notematrix%  [nmat, mstr] = mdlReadMidi(fn);%%  Input argument:%    FN = name of the MIDI file (string)%%  Output:%    NMAT = notematrix%    MSTR = midi structure (ala Ken Schutte's midi file reader)%%  Music Dynamics Lab read midi function created to replace the %  MIDI Toolbox function READMIDI%  New wrapper function for midi2nmat%  See License.txt%%%function [nmat, mstr] = mdlReadMidi(fn)mstr = mdlMidiToMStr(fn);nmat = mdlMStrToNMat(mstr);